const email = $input.item.json;

// Detect email source - multiple methods
let emailSource = '';

// Method 1: Try to detect from trigger data
try {
  const trigger1Data = $('Gmail Trigger (Account 1)').first();
  if (trigger1Data && trigger1Data.json && trigger1Data.json.id === email.id) {
    emailSource = 'account1';
  }
} catch (e) {
  // Not from trigger 1
}

if (!emailSource) {
  try {
    const trigger2Data = $('Gmail Trigger (Account 2)').first();
    if (trigger2Data && trigger2Data.json && trigger2Data.json.id === email.id) {
      emailSource = 'account2';
    }
  } catch (e) {
    // Not from trigger 2
  }
}

// Method 2: Fallback to recipient detection
if (!emailSource) {
  const toField = (email.to?.text || email.To || '').toLowerCase();
  const deliveredTo = (email.payload?.headers?.find(h => h.name.toLowerCase() === 'delivered-to')?.value || '').toLowerCase();
  const allRecipients = toField + ' ' + deliveredTo;

  // Customize these email patterns for your accounts
  if (allRecipients.includes('your-email-1')) {
    emailSource = 'account1';
  } else if (allRecipients.includes('your-email-2')) {
    emailSource = 'account2';
  } else {
    emailSource = 'account1'; // Default
  }
}

const emailFrom = email.from?.text || email.From || 'Unknown';
const emailSubject = email.subject || email.Subject || 'No Subject';
const emailBody = email.text || email.textPlain || email.snippet || '';

const systemPrompt = `You are an email classifier for job searching. Categorize emails into exactly one category:

JOB - Any email related to job searching:
  - Job alerts/postings from LinkedIn, Indeed, Glassdoor, company career pages
  - Application confirmations ("Thank you for applying", "We received your application")
  - Interview invitations or scheduling
  - Rejections ("We've decided to move forward with other candidates")
  - Offers, negotiations, or onboarding emails
  - Recruiter outreach ("We found your profile", "Exciting opportunity")

OTHER - Everything else:
  - Newsletters, promotions, marketing emails
  - Personal emails, social notifications
  - System notifications, receipts
  - Anything not directly related to job searching

IMPORTANT: For forwarded emails (subject starting with "Fwd:"), classify based on the ORIGINAL email content, not the forwarder.`;

return [{
  json: {
    model: 'gpt-5.2',
    input: [
      { role: 'system', content: systemPrompt },
      { role: 'user', content: `Classify this email:\n\nFrom: ${emailFrom}\nSubject: ${emailSubject}\nBody: ${emailBody}` }
    ],
    text: {
      format: {
        type: 'json_schema',
        name: 'email_classification',
        strict: true,
        schema: {
          type: 'object',
          properties: {
            category: { type: 'string', enum: ['JOB', 'OTHER'], description: 'The category of the email' }
          },
          required: ['category'],
          additionalProperties: false
        }
      }
    },
    reasoning: { effort: 'medium' },
    originalEmail: email,
    emailSource: emailSource,
    emailFrom: emailFrom,
    emailSubject: emailSubject
  }
}];

const response = $input.first().json;
const messageOutput = response.output.find(o => o.type === 'message');
if (!messageOutput) {
  throw new Error('No message output found in GPT response');
}
const resultText = messageOutput.content[0].text;
const result = JSON.parse(resultText);
const category = result.category;
const validCategories = ['JOB', 'OTHER'];
if (!validCategories.includes(category)) {
  throw new Error(`Invalid category: ${category}`);
}
return [{
  json: {
    ...response,
    category: category
  }
}];

const prepData = $('Prep Classify').item.json;
const gmail = prepData.originalEmail;

// Basic info
const emailFrom = prepData.emailFrom || gmail.from?.text || gmail.From || 'Unknown';
const emailSubject = prepData.emailSubject || gmail.subject || gmail.Subject || 'No Subject';

// Extract sender domain
const fromEmail = gmail.from?.value?.[0]?.address || '';
const senderDomain = fromEmail.split('@')[1] || 'unknown';

// Extract email body (priority: text > textPlain > html > snippet)
let emailBody = gmail.text || gmail.textPlain || '';
if (!emailBody && gmail.html) {
  emailBody = gmail.html
    .replace(/<style[^>]*>.*?<\/style>/gi, '')
    .replace(/<script[^>]*>.*?<\/script>/gi, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/\s+/g, ' ')
    .trim();
}
if (!emailBody) emailBody = gmail.snippet || '';
// Limit length to avoid too many tokens
if (emailBody.length > 2000) {
  emailBody = emailBody.substring(0, 2000) + '...';
}

// Gmail labels
const labels = (gmail.labelIds || []).join(', ');

// Is reply/forward
const isReply = emailSubject.toLowerCase().startsWith('re:');
const isForward = emailSubject.toLowerCase().startsWith('fwd:');

// Build enhanced user message
const userContent = `Analyze this job email:

From: ${emailFrom}
Sender Domain: ${senderDomain}
Subject: ${emailSubject}
Gmail Labels: ${labels}
Is Reply: ${isReply}
Is Forward: ${isForward}

Body:
${emailBody}`;

return [{
  json: {
    model: 'gpt-5.2',
    input: [
      {
        role: 'system',
        content: 'You are a job email analyzer. Extract structured information from job-related emails.\n\nDetermine the STATUS based on email content:\n- New Post: Job alerts, new job postings, recruiter outreach\n- Applied: Application confirmations ("Thank you for applying", "We received your application")\n- Interview: Interview invitations, scheduling, calendar invites\n- Rejected: Rejections ("decided not to move forward", "will not be proceeding", "other candidates")\n- Offer: Job offers, salary negotiations, onboarding\n- Follow Up: Status updates, follow-up requests, other communications\n\nIMPORTANT: Pay close attention to the email BODY content, not just the subject line. A subject saying "Follow-Up" might actually be a rejection if the body says "not moving forward".\n\nRecommend the NEXT_STEP:\n- Apply: For new job postings worth applying to\n- Prep: For interview invitations (prepare for interview)\n- Archive: For rejections or irrelevant emails\n- Follow Up: When action needed from you\n- Review: For offers or important decisions\n\nScore how well this matches an ML/AI Engineer role (1-10).'
      },
      {
        role: 'user',
        content: userContent
      }
    ],
    text: {
      format: {
        type: 'json_schema',
        name: 'job_analysis',
        strict: true,
        schema: {
          type: 'object',
          properties: {
            company: { type: 'string', description: 'Company name' },
            status: { type: 'string', enum: ['New Post', 'Applied', 'Interview', 'Rejected', 'Offer', 'Follow Up'], description: 'Current status of this job opportunity' },
            next_step: { type: 'string', enum: ['Apply', 'Prep', 'Archive', 'Follow Up', 'Review'], description: 'Recommended next action' },
            role: { type: 'string', description: 'Job title' },
            score: { type: 'integer', description: 'Match score 1-10 for ML/AI Engineer' },
            summary: { type: 'string', description: 'One sentence summary' },
            reason: { type: 'string', description: 'Why this score/action' }
          },
          required: ['company', 'status', 'next_step', 'role', 'score', 'summary', 'reason'],
          additionalProperties: false
        }
      }
    },
    reasoning: { effort: 'medium' },
    originalEmail: gmail
  }
}];

const messageOutput = $input.item.json.output.find(o => o.type === 'message');
const content = JSON.parse(messageOutput.content[0].text);
const prepData = $('Prep Classify').item.json;
const gmail = prepData.originalEmail;
const emailFrom = prepData.emailFrom || gmail.from?.text || gmail.From || 'Unknown';
const emailSubject = prepData.emailSubject || gmail.subject || gmail.Subject || 'No Subject';
const gmailAccount = prepData.emailSource || 'Unknown';
const emailId = gmail.id;

return [{
  json: {
    Company: content.company || 'Unknown',
    Status: content.status || 'New Post',
    'Next Step': content.next_step || 'Review',
    Role: content.role || 'Unknown',
    Score: content.score || 0,
    Summary: content.summary || '',
    Reason: content.reason || '',
    Timestamp: new Date().toISOString(),
    '_Subject': emailSubject,
    '_From': emailFrom,
    '_Gmail Account': gmailAccount,
    '_emailId': emailId,
    '_emailSource': gmailAccount
  }
}];

const prepData = $('Prep Classify').item.json;
const gmail = prepData.originalEmail;
const emailFrom = prepData.emailFrom || gmail.from?.text || gmail.From || 'Unknown';
const emailSubject = prepData.emailSubject || gmail.subject || gmail.Subject || 'No Subject';
const emailBody = gmail.text || gmail.textPlain || gmail.snippet || '';
const gmailAccount = prepData.emailSource || 'Unknown';
const emailId = gmail.id;
const cleanBody = emailBody.replace(/\s+/g, ' ').trim();
const summary = cleanBody.length > 150 ? cleanBody.substring(0, 150) + '...' : cleanBody;

return [{
  json: {
    Summary: summary,
    Timestamp: new Date().toISOString(),
    '_Subject': emailSubject,
    '_From': emailFrom,
    '_Gmail Account': gmailAccount,
    '_emailId': emailId,
    '_emailSource': gmailAccount
  }
}];
